(function checkAgGrid() {
    const gridDiv = document.querySelector('#leaderboard-grid');
    console.log('Checking for AG Grid...', gridDiv);
    if (!gridDiv || !gridDiv.querySelector('.ag-root-wrapper')) {
        console.log('AG Grid not found or not initialized, retrying in 300ms...');
        setTimeout(checkAgGrid, 300);
        return;
    }
    console.log('AG Grid found', gridDiv);
    const collectedData = new Set();
    const extractedRows = [];
    let resultsContainer = document.querySelector('.results-container');
    let rank = 1;
    if (!resultsContainer) {
        resultsContainer = document.createElement('div');
        resultsContainer.classList.add('results-container');
        document.body.appendChild(resultsContainer);
    }
    async function ensureColumnVisible(colId) {
        const gridCenter = document.querySelector('.ag-center-cols-viewport');
        const header = document.querySelector(`[col-id="${colId}"] .ag-header-cell`);
        if (!header || !gridCenter) return false;
        const headerRect = header.getBoundingClientRect();
        const gridRect = gridCenter.getBoundingClientRect();
        if (headerRect.left < gridRect.left || headerRect.right > gridRect.right) {
            gridCenter.scrollLeft = header.offsetLeft - 100;
            await new Promise(resolve => requestAnimationFrame(resolve));
        }
        return true;
    }
    async function getCellValue(row, colId) {
        await ensureColumnVisible(colId);
        let cell = row.querySelector(`[col-id="${colId}"]`);
        return cell ? cell.innerText.trim() : '';
    }
    async function extractRowData(rowIndex) {
        const rowData = {};
        const pinnedRow = document.querySelector(`.ag-pinned-left-cols-container .ag-row[row-index="${rowIndex}"]`);
        const centerRow = document.querySelector(`.ag-center-cols-viewport .ag-row[row-index="${rowIndex}"]`);
        if (!pinnedRow || !centerRow) return null;
        rowData.rank = rank++;
        rowData['#P'] = pinnedRow.querySelector(`[col-id="#P"]`)?.innerText.trim() || '';
        const modelCell = pinnedRow.querySelector(`[col-id="Model_Display"]`);
        rowData.model_name = modelCell?.innerText.trim() || '';
        rowData.model_url = modelCell?.querySelector('a')?.href || '';
        const columns = [
            { key: 'ugi_score', id: 'UGI ðŸ†' },
            { key: 'w10_score', id: 'W/10 ðŸ‘' },
            { key: 'Unruly', id: 'Unruly' },
            { key: 'coding_score', id: 'Coding ðŸ’»' },
            { key: 'polContro_score', id: 'Societal/Political' },
            { key: 'internet_score', id: 'Internet' },
        ];
        const promises = columns.map(col => getCellValue(centerRow, col.id).then(value => rowData[col.key] = value));
        await Promise.all(promises);
        return rowData;
    }
    function updateResultsContainer(rowData) {
        try {
            const row = document.createElement('div');
            row.classList.add('model-row');
            row.innerHTML = `
                <div class="rank">${rowData.rank}</div>
                <div class="P">${rowData['#P']}</div>
                <div class="model-name">
                    <a href="${rowData.model_url}" target="_blank">${rowData.model_name}</a>
                </div>
                <div class="ugi-score">${rowData.ugi_score}</div>
                <div class="w10-score">${rowData.w10_score}</div>
                <div class="unruly">${rowData.Unruly}</div>
                <div class="coding-score">${rowData.coding_score}</div>
                <div class="polcontro-score">${rowData.polContro_score}</div>
                <div class="internet">${rowData.internet_score}</div>
                <div class="test-date">${rowData.test_date}</div>
            `;
            resultsContainer.appendChild(row);
        } catch (error) {
            console.error('Error updating results container:', error);
        }
    }
    async function processVisibleRows() {
        const allRows = document.querySelectorAll('.ag-pinned-left-cols-container .ag-row');
        const newDataPromises = [];
        allRows.forEach(row => {
            const rowIndex = row.getAttribute('row-index');
            if (!rowIndex || collectedData.has(rowIndex)) return;
            collectedData.add(rowIndex);
            newDataPromises.push(extractRowData(rowIndex).then(rowData => {
                if (rowData) {
                    extractedRows.push(rowData);
                    updateResultsContainer(rowData);
                }
            }));
        });
        await Promise.all(newDataPromises);
    }
    async function scrollAndExtractRows() {
        const gridBody = gridDiv.querySelector('.ag-body-viewport');
        let lastScrollTop = -1;
        let scrollAttempts = 0;
        const maxScrollAttempts = 50;
        console.log('Starting optimized scrolling...');
        function smoothScroll() {
            return new Promise(resolve => {
                let step = 0;
                function scrollStep() {
                    if (step >= 10) return resolve();
                    gridBody.scrollTop += gridBody.clientHeight / 10;
                    step++;
                    requestAnimationFrame(scrollStep);
                }
                scrollStep();
            });
        }
        while (scrollAttempts < maxScrollAttempts) {
            console.log(`Scroll attempt ${scrollAttempts + 1}...`);
            const currentScrollTop = gridBody.scrollTop;
            await smoothScroll();
            if (gridBody.scrollTop === currentScrollTop) {
                console.log('No new rows detected. Stopping.');
                break;
            }
            lastScrollTop = gridBody.scrollTop;
            await processVisibleRows();
            scrollAttempts++;
        }
        console.log(`Finished scrolling. Total extracted rows: ${extractedRows.length}`);
    }
    function observeTableChanges() {
        const observer = new MutationObserver(async () => {
            await processVisibleRows();
        });
        observer.observe(gridDiv, { childList: true, subtree: true });
        console.log('MutationObserver attached.');
    }
    observeTableChanges();
    scrollAndExtractRows();
})()
);

