async function scrapePaginatedTable() {
    const delay = (ms) => new Promise(res => setTimeout(res, ms));
    const collectedData = [];

    async function fetchModelDetails(url) {
        try {
            const apiRes = await fetch(`https://llm-stats.com/api${url}`);
            const apiData = await apiRes.json();

            return {
                description: apiData.description,
                throughput: apiData.throughput,
                release_date: apiData.release_date,
                knowledge_cutoff: apiData.knowledge_cutoff,
                max_output_tokens: apiData.output_context_size,
                max_input_tokens: apiData.input_context_size,
                training_tokens: apiData.training_tokens,
                num_params: apiData.param_count,
                api_ref_link: apiData.api_ref_link,
                repo_link: apiData.repo_link,
                paper_link: apiData.paper_link,
                weights_link: apiData.weights_link,
                license: apiData.license
            };
        } catch (err) {
            console.error(`âŒ Error fetching model detail page (${url})`, err);
            return null;
        }
    }

    function extractRows() {
        const rows = document.querySelectorAll('tbody tr');
        rows.forEach(row => {
            const cols = row.querySelectorAll('td');
            const getText = (selector, el = row) => el.querySelector(selector)?.innerText.trim() || '';
            const getAttr = (selector, attr, el = row) => el.querySelector(selector)?.getAttribute(attr) || '';

            const rowData = {
                organization: getAttr("td:nth-child(1) img", "title"),
                model: getText("td:nth-child(2) a"),
                model_url: getAttr("td:nth-child(2) a", "href"),
                params: cols[3]?.innerText.trim() || '',
                input_cost: cols[5]?.innerText.trim() || '',
                output_cost: cols[6]?.innerText.trim() || '',
                gpqa: cols[7]?.innerText.trim() || '',
                mmlu: cols[8]?.innerText.trim() || '',
                mmlu_pro: cols[9]?.innerText.trim() || '',
                drop: cols[10]?.innerText.trim() || '',
                humaneval: cols[11]?.innerText.trim() || '',
                multimodal: cols[12]?.innerText.trim() || ''
            };

            if (rowData.model_url && !rowData.model_url.startsWith("http")) {
                rowData.model_url = location.origin + rowData.model_url;
            }
            collectedData.push(rowData);
        });
    }

    async function scrapeAllPages() {
        let hasNextPage = true;
        while (hasNextPage) {
            console.log("Extracting page data...");
            await delay(2000);
            extractRows();

            const nextButton = Array.from(document.querySelectorAll("button"))
                .find(btn => btn.textContent.trim() === "Next" && !btn.disabled);

            if (!nextButton) {
                console.log("No more pages to scrape.");
                hasNextPage = false;
                console.log("Total records collected:", collectedData.length);
            } else {
                console.log("Moving to next page...");
                nextButton.click();
                await delay(2000);
            }
        }
    }

    async function enrichWithModelDetails() {
        for (let i = 0; i < collectedData.length; i++) {
            const row = collectedData[i];
            if (row.model_url) {
                console.log(`ðŸ” Fetching details for ${row.model} (${row.model_url})...`);
                const urlPath = new URL(row.model_url).pathname;
                const details = await fetchModelDetails(urlPath);
                Object.assign(row, details);
            }
        }
